settings.custom_css:
#rivo-account-dashboard {
    margin: 0 auto;
    max-width: 1100px;
    padding-left: 50px;
    padding-right: 50px;
}

.rivo-account-block {
     padding: 0;
}

@media (min-width: 1400px) {
    #rivo-account-dashboard {
        max-width: 80vw;
    }
}

/* --- ACCOUNT CARD --- */

.rivo-account-card {
    border: 0 !important;
}

.rivo-account-card a {
    font-size: 15px;
}

.rivo-account-card-content-logo {
    max-height: 35px;
}

.rivo-account-card-content-logo img {
    max-height: 35px;
}

/* --- REFERRALS --- */

.rivo-account-referral-url-wrapper {
    background: #F7F5F2;
    padding: 5px;
}

input.rivo-coupon-code,
.rivo-form-input {
    font-size: 14px !important;
}

.rivo-account-referral-block-actions a {
    text-decoration: underline;
    font-weight: 500;
    color: #9A7E4A;
}

.rivo-account-referral-url-wrapper button {
    font-size: 15px;
}

.rivo-account-referral-block .btn.button-1.btn--solid.btn--large.btn--secondary {
    padding: 0px;
    font-size: 14px;
    min-width: 80px;
    min-height: 42px;
}

/* --- VIP TIERS  --- */

.rivo-account-progress-container {
    background: #F7F5F2;
}

.rivo-account-vip-tier-block {
    min-height: 162px;
}

/* --- REWARDS --- */

.rivo-account-points-purchase-card {}

.rivo-account-points-purchase-card-icon {
    width: fit-content;
}

.rivo-account-points-purchase-card-description {
    font-size: 14px;
}

.rivo-account-progress-labels {
    font-weight: 500;
    font-size: 14px;
}

div.rivo-account-points-purchases {
    margin: 0px !important;
}

@media screen and (max-width: 950px) {
    .rivo-account-card {
        min-height: 100%;
    }
    .rivo-account-rewards-block {
        min-height: 100% !important;
    }
    .rivo-account-referral-block {
        min-height: 100%;
    }
    .rivo-account-vip-tier-block {
        min-height: 100% !important;
    }
}

@media (max-width: 749px) {
    .rivo-account-card-content-logo img {
        max-height: 22px;
    }
}
