settings.custom_css:
/* --- ALL SECTIONS --- */

.rivo-copy-coupon .rivo-success-button:before {
    margin-left: -10px;
}

.rivo-lp-section-title {
    line-height: 1.5;
    margin-top: 16px;
    letter-spacing: normal;
}

.rivo-lp-section-subtitle {
    padding-top: 10px;
    padding-bottom: 10px;
    letter-spacing: normal;
}

.rivo-content-wrapper {
    padding-left: 15px;
    padding-right: 15px;
}

.rivo-card-title {
    text-transform: capitalize;
    letter-spacing: normal;
    font-weight: 500;
}

.rivo-card-description {
    padding-top: 0;
    letter-spacing: normal;
}

.rivo-arrow-icon {
    display: none;
}

.rivo-modal__title {
    font-size: 32px;
    letter-spacing: normal;
    font-weight: 400;
}

.rivo-modal-p {
    letter-spacing: normal;
}

.rivo-modal-p a {
    text-transform: capitalize;
    font-weight: 500;
    letter-spacing: normal;
}

/* --- HERO --- */

#rivo-lp-header {
    display: flex;
}

.rivo-hero-banner-text {
    height: 100%;
}

.rivo-hero-banner-wrapper {
    max-width: 1500px;
}

.rivo-hero-title {
    line-height: 1.2;
    font-weight: 400;
}

.rivo-hero-description {
    font-weight: 400;
    line-height: 1.3;
}

.rivo-hero-banner-media img {
    object-fit: cover;
    object-position: center;
}

/* --- HOW IT WORKS --- */

#rivo-how-it-works .rivo-lp-section-title {
    margin-top: 30px;
    margin-bottom: 20px;
}

.rivo-how-it-works-step-title {
    font-weight: 400;
    color: #212121;
    letter-spacing: normal;
}

.rivo-how-it-works-step-subtitle {
    font-weight: 400;
    line-height: 1.4;
    color: #212121;
    letter-spacing: normal;
}

.rivo-how-it-works-step-image img {
    margin: 0 auto;
}

/* --- WAYS TO EARN --- */

#rivo-ways-to-earn .rivo-card-title {
    margin-bottom: 4px;
    line-height: 1.2;
}

#rivo-ways-to-earn .rivo-card-description {
    line-height: 1.2;
    padding-right: 20px;
    padding-left: 20px;
}

#rivo-ways-to-earn .rivo-arrow-icon {
    display: none;
}

/*
#rivo-ways-to-earn .rivo-card {
    min-height: 160px;
}
*/

#rivo-ways-to-earn .rivo-card {
    border: 1px solid #D1CDC4;
}

#rivo-ways-to-earn .rivo-card:hover {
    background-color: #fff;
    border: 1px solid #fff;
    box-shadow: 0 0 25px #ebe9e8;
}

/* --- WAYS TO REDEEM (AND ACTIVITY) --- */

#rivo-ways-to-redeem img.custom-icon,
#rivo-lp-my-rewards img.custom-icon {
    width: 120px !important;
    height: 120px !important;
}

#rivo-ways-to-redeem .rivo-card-title,
#rivo-lp-my-rewards .rivo-card-title {
    margin-bottom: 0px;
    line-height: 0.8;
}

#rivo-ways-to-redeem .rivo-arrow-icon,
#rivo-lp-my-rewards .rivo-arrow-icon {
    display: none;
}

#rivo-ways-to-redeem .rivo-card,
#rivo-lp-my-rewards .rivo-card {
    background-color: transparent;
    gap: 8px;
}

#rivo-ways-to-redeem .rivo-card:hover,
#rivo-lp-my-rewards .rivo-card:hover {
    background-color: #fff;
}

/* --- VIP TIERS PROGRESS BAR --- */

.rivo-vip-tier-progress-container {
    width: 50%;
}

@media (max-width: 768px) {
    .rivo-vip-tier-progress-container {
        width: 95%;
    }
    /* --- LOGGED IN PROGRESS BAR --- */
    .rivo-vip-tier-columns .rivo-vip-tier-progress-description {
        display: block !important;
        padding-left: 0px !important;
    }
}

.rivo-account-progress-container {
    background: #EAE6DD;
}

.rivo-account-progress-bar {
    background-color: #212121;
}

.rivo-progress-current-tier-label,
.rivo-progress-next-tier-label {
    font-size: 14px;
}

.rivo-vip-tier-progress-container.rivo-vip-tier-columns {
    justify-content: center;
}

/* --- ACTIVITY --- */

.rivo-completed {
    display: none;
}

#rivo-lp-explainer .rivo-lp-section-subtitle {
    color: #212121;
}

.rivo-points-expiry {
    padding: 10px;
    background: #FBFAF8;
    width: fit-content;
    margin: 0 auto;
    border-radius: 0px;
    font-size: 13px;
}

#rivo-lp-my-rewards .rivo-lp-content {
    padding: 0px;
    margin: 0;
    background-color: #fff;
    border-radius: 0px;
    max-width: 100% !important;
}

#rivo-lp-my-rewards.rivo-content-wrapper {
    padding-left: 0px !important;
    padding-right: 0px !important;
}

input.rivo-coupon-code {
    font-size: 16px !important;
    border-radius: 0px;
}

#points-logs-activity th,
#points-logs-activity td {
    font-size: 14px;
    border-bottom: 1px solid #D1CDC4;
}

#points-logs-activity {
    border: none !important;
}

#points-logs-activity th {
    color: #fff;
    background-color: #212121 !important;
}

/* Logged in - Activity Table - Odd rows (1st, 3rd, etc) */

#points-logs-activity tr:nth-child(odd) {
    background-color: transparent !important;
}

/* Logged in - Activity Table - Even rows (2nd, 4th, etc) */

#points-logs-activity tr:nth-child(even) {
    background-color: #FCFAF8;
}

/* --- VIP TIERS --- */

table.rivo-vip-tier-table {
    margin-bottom: 60px;
}

table.rivo-vip-tier-table table tr > th,
table tr > td {
    border-right: #fff !important;
}

span.rivo-th-tier-threshold {
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Table Header */

th.rivo-vip-tier-table-thead-first-cell,
th.rivo-vip-tier-table-thead-tier-cell {}

span.rivo-th-tier-threshold {
    color: #AB8C52;
    letter-spacing: 2px;
    font-weight: 500;
    text-transform: uppercase;
}

/* First column */

td.rivo-vip-tier-table-row-perk-name {
    text-align: left;
    font-weight: 500;
}

span.rivo-th-tier-name {
    color: #212121;
}

th.rivo-vip-tier-table-thead-tier-cell {}

tr.rivo-vip-tier-table-tbody-row td.rivo-tier-column {
    color: #212121;
}

.rivo-vip-tier-table-thead-row th {
    border-right: #fff !important;
    border-bottom: none;
}

/* — VIP Tiers Table Scroll — */

@media (max-width: 768px) {
    .rivo-how-it-works-step-image img {
        height: 125px;
        margin: 0 auto;
    }
    .rivo-vip-tier-container {
        width: 100%;
        overflow: auto;
    }
    table.rivo-vip-tier-table td,
    table.rivo-vip-tier-table th {
        width: 120px;
        height: 50px;
    }
    .rivo-vip-tier-table-thead-first-cell {
        background: #fff;
    }
    .rivo-vip-tier-table-thead-first-cell,
    td.rivo-vip-tier-table-row-perk-name {
        position: sticky;
        left: 0;
    }
}

/* --- SCROLL BAR --- */

/* Styles for WebKit-based browsers (Chrome, Safari, newer versions of Edge) */

.rivo-lp-cards-container::-webkit-scrollbar,
.rivo-vip-tier-container::-webkit-scrollbar {
    width: 10px;
    height: 8px;
}

.rivo-lp-cards-container::-webkit-scrollbar-track,
.rivo-vip-tier-container::-webkit-scrollbar-track {
    background: #EAE6DD;
    border-radius: 10px;
}

.rivo-lp-cards-container::-webkit-scrollbar-thumb,
.rivo-vip-tier-container::-webkit-scrollbar-thumb {
    background: #212121;
    border-radius: 10px;
}

.rivo-lp-cards-container::-webkit-scrollbar-thumb:hover,
.rivo-vip-tier-container::-webkit-scrollbar-thumb:hover {
    background: #baa899;
}

/* --- REFERRAL --- */

iframe.rivo-referral {
    margin: 0 auto;
    border-radius: 0px;
    background: #EAE6DD;
    /* max-width: 1400px; */
}

#rivo-lp-referral-campaign.rivo-content-wrapper {
    padding: 0px !important;
}

/* —-- MOBILE Media queries —-- */

@media screen and (max-width: 769px) {
    /* —-- ALL SECTIONS —-- */
    .rivo-lp-section-title {
        line-height: 1.2;
        font-size: 26px;
    }
    .rivo-lp-section-subtitle {
        line-height: 1.2;
        font-size: 16px;
    }
    .rivo-lp-cards-container {
        width: 100% !important;
    }
    #rivo-lp-header .rivo-lp-content {
        padding-left: 0;
        padding-right: 0;
    }
    #rivo-lp-referral-campaign .rivo-lp-content {
        padding-left: 0;
        padding-right: 0;
    }
    /* --- WAYS TO REDEEM (AND ACTIVITY) --- */
    #rivo-ways-to-redeem .rivo-card-title,
    #rivo-lp-my-rewards .rivo-card-title {
        line-height: 1.2 !important;
        font-size: 14px;
    }
    #rivo-ways-to-redeem .rivo-card-description,
    #rivo-lp-my-rewards .rivo-card-description {
        font-size: 14px;
    }
    #rivo-ways-to-redeem .rivo-lp-cards-container {
        flex-wrap: wrap;
    }
    #points-logs-activity {
        table-layout: auto;
    }
    table.rivo-vip-tier-table {
        border-collapse: separate;
        border-spacing: 0;
    }
    th.rivo-vip-tier-table-thead-tier-cell {
        padding: 1rem 1rem 0rem !important;
    }
}

@media (max-width: 749px) {
    /* —-- HERO BANNER —-- */
    #rivo-lp-header {
        display: block;
    }
    .rivo-hero-banner-text {
        padding-top: 0px;
        padding-bottom: 40px;
    }
    /* —-- ALL SECTIONS —-- */
    .rivo-content-wrapper {
        padding-left: 10px;
        padding-right: 10px;
    }
    .rivo-mobile-banner-overlay .rivo-lp-content {
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
    }
    /* --- HOW IT WORKS --- */
    .rivo-how-it-works-step-title {
        font-size: 22px;
    }
    .rivo-how-it-works-step-image img {
        width: 80px;
        height: 80px;
    }
    #rivo-how-it-works .rivo-lp-section-title {
        margin-bottom: 0px;
    }
    #rivo-how-it-works {
        margin-bottom: 0px;
    }
    /* --- WAYS TO EARN --- */
    #rivo-ways-to-earn img.custom-icon {
        width: 50px !important;
        height: 50px !important;
    }
    /* --- VIP TIERS --- */
    .rivo-vip-tier-table-row-perk-name {
        font-size: 12px !important;
        line-height: 1.5;
        padding: 15px !important;
    }
    span.rivo-th-tier-name {
        font-size: 14px;
    }
    span.rivo-th-tier-threshold {
        font-size: 10px;
        letter-spacing: 0px;
    }
    td.rivo-vip-tier-table-row-perk-value {
        font-size: 12px;
    }
    #rivo-vip-tiers img.custom-icon {
        width: 60px !important;
        height: 60px !important;
    }
    .rivo-vip-tier-container {
        margin-top: 0px;
    }
    /* --- WAYS TO EARN --- */
    .rivo-card-title {
        font-size: 15px;
    }
    .rivo-card-description {
        font-size: 13px !important;
        line-height: 1.1;
    }
    #rivo-ways-to-redeem img.custom-icon,
    #rivo-lp-my-rewards img.custom-icon {
        width: 90px !important;
        height: 90px !important;
    }
    /* --- FAQs --- */
    .rivo-faq-question {
        font-size: 15px !important;
    }
    .rivo-faq-answer {
        font-size: 14px !important;
    }
    /* --- MODAL --- */
    .rivo-modal__title {
        font-size: 26px;
        font-weight: 500;
    }
    .rivo-modal p {
        font-size: 16px !important;
    }
}

/* --- DESKTOP Media queries ---*/

@media (min-width: 770px) {
    /* —-- HERO BANNER —-- */
    .rivo-hero-banner-wrapper {
        padding: 5rem;
    }
    .rivo-hero-banner-text {
        display: flex;
        flex-direction: column;
    }
    .rivo-hero-description {
        margin-top: 2px;
        margin-bottom: 6px;
    }
    .rivo-hero-buttons a {
        min-width: 160px;
        margin-top: 20px;
    }
    /* --- WAYS TO REDEEM --- */
    #rivo-ways-to-redeem .rivo-lp-cards-container {
        /* flex-wrap: nowrap; */
        /* max-width: 1200px; */
    }
    .rivo-lp-cards-container {
        padding-left: 40px;
        padding-right: 40px;
    }
    #rivo-lp-referral-campaign .rivo-lp-content {
        max-width: 100%;
    }
}

#rivo-ways-to-redeem .rivo-lp-cards-container { 
  max-width: 1200px !important;
  flex-wrap: nowrap !important;
} 

#rivo-lp-my-rewards.rivo-content-wrapper {
   padding-top: 0px;
}


.rivo-how-it-works-step-item img {
    width: 80px;
    height: 80px;
}

#rivo-referrals-gifts.rivo-content-wrapper {
    background-color: #fbfaf8 !important;
    padding-top: 30px;
    padding-bottom: 20px;
}

.rivo-rich-text li {
    border-color:  #EAE7DC !important;
    color: #212121 !important;
}

@media (max-width: 768px) {
    .rivo-image-with-text-column img {
        max-height: fit-content !important;
    }
#rivo-lp-explainer .rivo-content-wrapper {
     padding-top: 0px;
    }
}
